class Random{static intInRange=(n,t)=>Math.floor(Math.random()*(t-n+1)+n);static floatInRange=(n,t)=>Math.random()*(t-n)+n;static boolean=()=>Math.random()<.5;static chooseFromArray=n=>n[Math.floor(Math.random()*n.length)];static shuffleArray=n=>{const t=[...n];for(let n=t.length-1;n>0;n--){const a=Math.floor(Math.random()*(n+1))[t[a]]=[t[a],t[n]]}return t};static randomChar=()=>{const n=Random.intInRange(65,90);return String.fromCharCode(n)};static randomString=n=>Array.from({length:n},Random.randomChar).join("");static randomAlphaString=n=>Array.from({length:n},(()=>{const n=Random.intInRange(65,90);return String.fromCharCode(n)})).join("");static randomAlphaNumString=n=>{const t="ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";return Array.from({length:n},(()=>t[Random.intInRange(0,t.length-1)])).join("")};static randomNumString=n=>Array.from({length:n},(()=>Random.intInRange(0,9))).join("")}